import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { initiatePayment } from '../../api/payments';
import './PaymentForm.css'; // Assuming you will create a CSS file for styling

const PaymentForm = () => {
    const [amount, setAmount] = useState('');
    const [currency, setCurrency] = useState('USD');
    const [paymentMethod, setPaymentMethod] = useState('stripe');
    const [error, setError] = useState('');
    const history = useHistory();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const response = await initiatePayment({ amount, currency, paymentMethod });
            if (response.success) {
                history.push('/payment-status', { payment: response.payment });
            } else {
                setError(response.message || 'Payment initiation failed');
            }
        } catch (err) {
            setError('An error occurred while processing your payment');
        }
    };

    return (
        <div className="payment-form-container">
            <h2>Payment Form</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="amount">Amount:</label>
                    <input
                        type="number"
                        id="amount"
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="currency">Currency:</label>
                    <select
                        id="currency"
                        value={currency}
                        onChange={(e) => setCurrency(e.target.value)}
                    >
                        <option value="USD">USD</option>
                        <option value="EUR">EUR</option>
                        <option value="GBP">GBP</option>
                    </select>
                </div>
                <div className="form-group">
                    <label htmlFor="paymentMethod">Payment Method:</label>
                    <select
                        id="paymentMethod"
                        value={paymentMethod}
                        onChange={(e) => setPaymentMethod(e.target.value)}
                    >
                        <option value="stripe">Stripe</option>
                        <option value="paypal">PayPal</option>
                    </select>
                </div>
                {error && <div className="error-message">{error}</div>}
                <button type="submit" className="submit-button">Pay Now</button>
            </form>
        </div>
    );
};

export default PaymentForm;
